DROP TABLE PUBLIC.VIEW_API_LAWYERS;

CREATE VIEW VIEW_API_LAWYERS AS
SELECT * FROM LAWYER_DETAILS;
----------------------------------------------------------------
drop table VIEW_LAWYER_RESUME_API_MODEL;

CREATE VIEW VIEW_LAWYER_RESUME_API_MODEL AS 
SELECT ROWNUM() AS ID, LD.*, E.EDUCATION_ID, E.DEGREE, E.GRADUATED_YEAR, E.SCHOOL_NAME,
ED.ENDORSEMENT_ID , ED.DESCRIPTION  AS ED_DESCRIPTION, ED.ENDORSEMENT_DATE , ED.RELATIONSHIP ,
LS.LICENSE_ID , LS.ACQUIRED_DATE , LS.EXPIRED_DATE , LS.STATE , LS.STATUS ,
P.PUBLICATION_ID, P.NAME, P.PUBLICATION_DATE , P.TITLE AS P_TITLE,
SE.SPEAKING_ENGAGEMENT_ID , SE.CONFERENCE_DATE , SE.CONFERENCE_NAME , SE.TITLE AS SE_TITLE ,
WE.WORK_EXPERIENCE_ID , WE.COMPANY_NAME , WE.DURATION , WE.TITLE AS WE_TITLE
FROM LAWYER_DETAILS LD
LEFT JOIN EDUCATION E ON LD.LAWYER_CODE = E.LAWYER_CODE
LEFT JOIN ENDORSEMENTS ED ON LD.LAWYER_CODE = ED.LAWYER_CODE
LEFT JOIN LICENSES LS ON LS.LAWYER_CODE = E.LAWYER_CODE
LEFT JOIN PUBLICATIONS P ON P.LAWYER_CODE = E.LAWYER_CODE
LEFT JOIN SPEAKING_ENGAGEMENTS SE ON SE.LAWYER_CODE = E.LAWYER_CODE
LEFT JOIN WORK_EXPERIENCE WE ON WE.LAWYER_CODE = E.LAWYER_CODE;


